    private void dniCampoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_dniCampoActionPerformed
        dniCampo.setText(agenda.dniRetorna(agenda.valorIndice()));
    }//GEN-LAST:event_dniCampoActionPerformed

    private void atrasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_atrasActionPerformed
        //if para que no pase a menos de 0 y se actualizan los datos de cada campo de texto
        if(agenda.valorIndice()>0){
            int aux=agenda.valorIndice();
            aux--;
            agenda.cambiarIndice(aux);
            indice.setText(""+aux);
            dniCampo.setText(agenda.dniRetorna(agenda.valorIndice()));
            nombreCampo.setText(agenda.nombreRetorna(agenda.valorIndice()));
            apellidoCampo.setText(agenda.apellidoRetorna(agenda.valorIndice()));
            direccionCampo.setText(agenda.direccionRetorna(agenda.valorIndice()));
            telefonoCampo.setText(agenda.telefonoRetorna(agenda.valorIndice()));
            fecNacCampo.setText(agenda.fecNacRetorna(agenda.valorIndice()));
        }
        
    }//GEN-LAST:event_atrasActionPerformed

    private void grabarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_grabarActionPerformed
        //se guardan los datos puestos en los campos
        String dni=dniCampo.getText();
        String nombre=nombreCampo.getText();
        String apellido=apellidoCampo.getText();
        String direccion=direccionCampo.getText();
        String telefono=telefonoCampo.getText();
        String fecNac=fecNacCampo.getText();
        
        agenda.dniGuardar(dni, agenda.valorIndice());
        agenda.nombreGuardar(nombre, agenda.valorIndice());
        agenda.apellidoGuardar(apellido, agenda.valorIndice());
        agenda.direccionGuardar(direccion, agenda.valorIndice());
        agenda.telefooGuardar(telefono, agenda.valorIndice());
        agenda.fecNacGuardar(fecNac, agenda.valorIndice());
    }//GEN-LAST:event_grabarActionPerformed

    private void nombreCampoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nombreCampoActionPerformed
        nombreCampo.setText(agenda.nombreRetorna(agenda.valorIndice()));
    }//GEN-LAST:event_nombreCampoActionPerformed

    private void siguienteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_siguienteActionPerformed
       //Se verifica que no sobrepase el numero 9 y se actualizan los datos
        if(agenda.valorIndice()<=8){
            int aux =agenda.valorIndice();
            aux++;
            agenda.cambiarIndice(aux);
            indice.setText(""+aux);
            dniCampo.setText(agenda.dniRetorna(agenda.valorIndice()));
            nombreCampo.setText(agenda.nombreRetorna(agenda.valorIndice()));
            apellidoCampo.setText(agenda.apellidoRetorna(agenda.valorIndice()));
            direccionCampo.setText(agenda.direccionRetorna(agenda.valorIndice()));
            telefonoCampo.setText(agenda.telefonoRetorna(agenda.valorIndice()));
            fecNacCampo.setText(agenda.fecNacRetorna(agenda.valorIndice()));
            
        }
        
    }//GEN-LAST:event_siguienteActionPerformed

